import { useState, useEffect } from "react";
import axios from "axios";
import { useGlobalState } from "../contexts/GlobalContext";
import { spotifyApi } from "../pages/Home";

export default function useAuth(code) {
  const instance = axios.create({
    timeout: 1000,
    headers: { "Content-Type": "application/json" },
  });

  const [accessToken, setAccessToken] = useState();
  const [refreshToken, setRefreshToken] = useState();
  const [expiresIn, setExpiresIn] = useState();
  const { setToken } = useGlobalState();

  useEffect(() => {
    instance
      .post(`http://localhost:9000/api/login`, {
        code,
      })
      .then((res) => {
        console.log("Response", res);
        setAccessToken(res.data.accessToken);
        setToken(res.data.accessToken);
        localStorage.setItem("api-key", res.data.accessToken);

        setRefreshToken(res.data.refreshToken);
        setExpiresIn(res.data.expiresIn);
        window.history.pushState({}, null, "/");
      })
      .catch((err) => {
        console.log(
          `Error posting to ${process.env.REACT_APP_CLEANIFY_BACKEND_URL}/login`,
          "code is",
          code,
          "error is",
          err
        );
        window.location = "/";
      });
  }, [code, instance, setToken]);

  useEffect(() => {
    if (!refreshToken || !expiresIn) return;
    const interval = setInterval(() => {
      axios
        .post(`${process.env.CLEANIFY_BACKEND_URL}/refresh`, {
          refreshToken,
        })
        .then((res) => {
          setAccessToken(res.data.accessToken);
          setToken(res.data.accessToken);
          spotifyApi.setAccessToken(res.data.accessToken);
          localStorage.setItem("api-key", res.data.accessToken);
          setExpiresIn(res.data.expiresIn);
        })
        .catch(() => {
          window.location = "/";
        });
    }, (expiresIn - 60) * 1000);

    return () => clearInterval(interval);
  }, [refreshToken, expiresIn, setToken]);

  return accessToken;
}
